                    function validate () {
                        var ok = true;

                        if (jQuery('#opstinaId').val() == "") {
                            ok = false;
                        }

                        if (jQuery('#vrstanepokretnosti').val() == null) {
                            ok = false;
                        }

                        if (ok) {
